/// Central instance to access all Dns related resource activities
///
/// # Examples
///
/// Instantiate a new hub
///
/// ```test_harness,no_run
/// extern crate hyper;
/// extern crate hyper_rustls;
/// extern crate google_dns1 as dns1;
/// use dns1::{Result, Error};
/// # async fn dox() {
/// use dns1::{Dns, FieldMask, hyper_rustls, hyper_util, yup_oauth2};
///
/// // Get an ApplicationSecret instance by some means. It contains the `client_id` and
/// // `client_secret`, among other things.
/// let secret: yup_oauth2::ApplicationSecret = Default::default();
/// // Instantiate the authenticator. It will choose a suitable authentication flow for you,
/// // unless you replace  `None` with the desired Flow.
/// // Provide your own `AuthenticatorDelegate` to adjust the way it operates and get feedback about
/// // what's going on. You probably want to bring in your own `TokenStorage` to persist tokens and
/// // retrieve them from storage.
/// let auth = yup_oauth2::InstalledFlowAuthenticator::builder(
///     secret,
///     yup_oauth2::InstalledFlowReturnMethod::HTTPRedirect,
/// ).build().await.unwrap();
///
/// let client = hyper_util::client::legacy::Client::builder(
///     hyper_util::rt::TokioExecutor::new()
/// )
/// .build(
///     hyper_rustls::HttpsConnectorBuilder::new()
///         .with_native_roots()
///         .unwrap()
///         .https_or_http()
///         .enable_http1()
///         .build()
/// );
/// let mut hub = Dns::new(client, auth);
/// // You can configure optional parameters by calling the respective setters at will, and
/// // execute the final call using `doit()`.
/// // Values shown here are possibly random and not representative !
/// let result = hub.managed_zones().list("project")
///              .page_token("takimata")
///              .max_results(-52)
///              .dns_name("duo")
///              .doit().await;
///
/// match result {
///     Err(e) => match e {
///         // The Error enum provides details about what exactly happened.
///         // You can also just use its `Debug`, `Display` or `Error` traits
///          Error::HttpError(_)
///         |Error::Io(_)
///         |Error::MissingAPIKey
///         |Error::MissingToken(_)
///         |Error::Cancelled
///         |Error::UploadSizeLimitExceeded(_, _)
///         |Error::Failure(_)
///         |Error::BadRequest(_)
///         |Error::FieldClash(_)
///         |Error::JsonDecodeError(_, _) => println!("{}", e),
///     },
///     Ok(res) => println!("Success: {:?}", res),
/// }
/// # }
/// ```
#[derive(Clone)]
pub struct Dns<C> {
    pub client: common::Client<C>,
    pub auth: Box<dyn common::GetToken>,
    _user_agent: String,
    _base_url: String,
    _root_url: String,
}

impl<C> common::Hub for Dns<C> {}

impl<'a, C> Dns<C> {
    pub fn new<A: 'static + common::GetToken>(client: common::Client<C>, auth: A) -> Dns<C> {
        Dns {
            client,
            auth: Box::new(auth),
            _user_agent: "google-api-rust-client/6.0.0".to_string(),
            _base_url: "https://dns.googleapis.com/".to_string(),
            _root_url: "https://dns.googleapis.com/".to_string(),
        }
    }

    pub fn changes(&'a self) -> ChangeMethods<'a, C> {
        ChangeMethods { hub: self }
    }
    pub fn dns_keys(&'a self) -> DnsKeyMethods<'a, C> {
        DnsKeyMethods { hub: self }
    }
    pub fn managed_zone_operations(&'a self) -> ManagedZoneOperationMethods<'a, C> {
        ManagedZoneOperationMethods { hub: self }
    }
    pub fn managed_zones(&'a self) -> ManagedZoneMethods<'a, C> {
        ManagedZoneMethods { hub: self }
    }
    pub fn policies(&'a self) -> PolicyMethods<'a, C> {
        PolicyMethods { hub: self }
    }
    pub fn projects(&'a self) -> ProjectMethods<'a, C> {
        ProjectMethods { hub: self }
    }
    pub fn resource_record_sets(&'a self) -> ResourceRecordSetMethods<'a, C> {
        ResourceRecordSetMethods { hub: self }
    }
    pub fn response_policies(&'a self) -> ResponsePolicyMethods<'a, C> {
        ResponsePolicyMethods { hub: self }
    }
    pub fn response_policy_rules(&'a self) -> ResponsePolicyRuleMethods<'a, C> {
        ResponsePolicyRuleMethods { hub: self }
    }

    /// Set the user-agent header field to use in all requests to the server.
    /// It defaults to `google-api-rust-client/6.0.0`.
    ///
    /// Returns the previously set user-agent.
    pub fn user_agent(&mut self, agent_name: String) -> String {
        std::mem::replace(&mut self._user_agent, agent_name)
    }

    /// Set the base url to use in all requests to the server.
    /// It defaults to `https://dns.googleapis.com/`.
    ///
    /// Returns the previously set base url.
    pub fn base_url(&mut self, new_base_url: String) -> String {
        std::mem::replace(&mut self._base_url, new_base_url)
    }

    /// Set the root url to use in all requests to the server.
    /// It defaults to `https://dns.googleapis.com/`.
    ///
    /// Returns the previously set root url.
    pub fn root_url(&mut self, new_root_url: String) -> String {
        std::mem::replace(&mut self._root_url, new_root_url)
    }
}

